    public  static ArrayList<CagarModel> getListData(){
        CagarModel cagarModel = null;
        ArrayList<CagarModel> cagarModelArrayList = new ArrayList<>();
        for  (int i= 0; i <dataCagar.length; i++){
            cagarModel = new CagarModel();
            cagarModel.setLogoCagar(dataFotoCagar[i][0]);
            cagarModel.setJudulCagar(dataCagar[i][0]);
            cagarModel.setDeskripsiCagar(deskripsiCagar[i][0]);
            cagarModelArrayList.add(cagarModel);
        }
        return cagarModelArrayList;
    }
